import 'dart:io';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import '../productModule/ui/sub_ui/cartscreen.dart';import '../productModule/ui/sub_ui/checkout.dart';import '../productModule/ui/sub_ui/orderdetails.dart';import '../productModule/ui/sub_ui/ordersuccess.dart';import '../productModule/ui/sub_ui/product_detail.dart';void openScreenAsLeftToRight(BuildContext context, Widget targetWidget) =>    openScreenAsPlatformWiseRoute(context, targetWidget, isFullScreen: true);void openProductDetails(BuildContext context) {  openScreenAsPlatformWiseRoute(context, ProductDetails());}void openAddCart(BuildContext context) {  openScreenAsPlatformWiseRoute(context, AddCart());}void openCheckOut(BuildContext context) {  openScreenAsPlatformWiseRoute(context, CheckOut());}void openOrderSuccess(BuildContext context) {  openScreenAsPlatformWiseRoute(context, OrderSuccess());}// void openOrderDetails(BuildContext context) {//   openScreenAsPlatformWiseRoute(context, OrderDetails());// }void openScreenAsPlatformWiseRoute(BuildContext context, Widget targetWidget,        {bool isFullScreen = false}) async =>    await Platform.isIOS        ? Navigator.of(context).push(CupertinoPageRoute(            builder: (BuildContext context) => targetWidget,            fullscreenDialog: isFullScreen))        : Navigator.of(context).push(MaterialPageRoute(            builder: (BuildContext context) => targetWidget,            fullscreenDialog: isFullScreen));